def get_dirs(commands: list[str]) -> Directory:
    """Create dir tree from '/'. Return all dirs"""
    root = Directory("/")
    dirstack = [root]

    for i, line in enumerate(commands):
        cur_dir = dirstack[-1]
        match line.split():
            case ("$", "ls"):
                continue
            case ("dir", dirname):
                try:
                    cur_dir[dirname]  # Should fail, unless twice ls same dir
                    print("*********** {dirname} already in {cur_dir.name}!")
                except KeyError:
                    cur_dir.append(Directory(dirname))
            case ("$", "cd", dirname):
                match dirname:
                    case "/":
                        dirstack = [root]
                    case "..":
                        dirstack.pop()
                    case _: 
                        dirstack.append(cur_dir[dirname])
                depth = len(dirstack) - 1
                print(f'{"âš" * depth}\tfrom cd {dirname}\t in line {i}')
            case (size, name):
                f = File(name, int(size))
                cur_dir.append(f)
            case _:
                raise ValueError(f"Unrecognized line: '{line}'")
    return root
            
