#| code-fold: true
def sum_cutoff(start: Directory, cutoff=100_000) -> int:
    subdirs = get_all_subdirs(start)
    sizes = [x[1] for x in subdirs if x[1] < 100_000]
    return sum(x for x in sizes if x <= cutoff)
